function CourseList (props) {
  const [course, setCourse] = React.useState ("");
  const [courseList, setCourseList] = React.useState([])

  function addCourseToList (e) {
    //TOOO
    setCourseList([...courseList, course]);
    setCourse("");
  }

  function clearCourseList () {
    setCourseList([])
  }

  return (
    <div>
      <Header color="blue">Course List</Header>
      <input placeholder="Enter a course"
        value={course}
        onChange={e => setCourse(e.target.value)}
        />
      <Button onClick={addCourseToList} label="Add Course" disabled={!course}/>
      {
        courseList.length != 0 ?
        <Button onClick={clearCourseList} label="Clear List" color='red' />
        : null
      }
      <ul>
        {
          courseList.map(item=><li>{item}</li>)
        }
      </ul>
    </div>
  )
}

function Button (props) {
  return (
    <button style={{color : props.color || 'green'}} onClick = {props.onClick} disabled = {props.disabled}>
      {props.label}
    </button>
  )
}

function Header ({children, color}) {
  return (
    <h1 style={{color}}>
      {children}
    </h1>
  )
}

